<script>

    function add(str)
    {
        values = 0;

        values = parseInt(document.getElementById(str + '_melayu').value) +
                parseInt(document.getElementById(str + '_cina').value) +
                parseInt(document.getElementById(str + '_india').value) +
                parseInt(document.getElementById(str + '_singh').value) +
                parseInt(document.getElementById(str + '_pathan').value) +
                parseInt(document.getElementById(str + '_ptetap').value) +
                parseInt(document.getElementById(str + '_llb').value) +
                parseInt(document.getElementById(str + '_wanita').value);

        document.getElementById(str + '_jumlah').value = parseInt(values);
    }

</script>